import { useState } from "react";
import Expenseitem from "./Expenseitems"
import "./ExpensesList.css"

const ExpenseList = (temp) => {
    console.log(temp.items);
const [props,changeprop] = useState(temp.items);
const buttonhandler = (idkey) => {
    changeprop((props)=> {return props.filter(function(expense){ 
        return expense.id !== idkey; 
    })}
    );
    
}; 
console.log(props);
if(props.length===0){
    return (<h2 className="expenses-list__fallback">No expenses.</h2>)
}
   
       return (<div className="expenses-list">{props.map(expense => <Expenseitem key = {expense.id} title={expense
        .title} amount={expense.amount} date={expense.date} onclickhandler={buttonhandler}/>)}</div>);
   
};
export default ExpenseList;
