import Chart from "../Chart/Chart";
const ExpensesChart = (props) => {
    const chartDatapoints = [
        {label : 'Jan', value : 0},
        {label : 'Feb', value : 0},
        {label : 'Mar', value : 0},
        {label : 'Apr', value : 0},
        {label : 'May', value : 0},
        {label : 'Jun', value : 0},
        {label : 'Jul', value : 0},
        {label : 'Aug', value : 0},
        {label : 'Sept', value : 0},
        {label : 'Oct', value : 0},
        {label : 'Nov', value : 0},
        {label : 'Dec', value : 0},

    ];
    for(let expense of props.expenses){
        const month = expense.date.getMonth();
        chartDatapoints[month].value+=expense.value;
    }
    return <Chart datapoint = {chartDatapoints} />;

};
export default ExpensesChart;
